fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();

    let (n, m) = lines
        .next()
        .unwrap()
        .split_whitespace()
        .map(|s| s.parse::<usize>().unwrap())
        .collect::<(usize, usize)>();

    let mut dp = vec![0; n + 1];
