    for _ in 0..m {
        let (s, t) = lines
            .next()
            .unwrap()
            .split_whitespace()
            .map(|s| s.parse::<usize>().unwrap())
            .collect::<(usize, usize)>();

        for i in s..t {
            dp[i] = (dp[i] + dp[i - 1]) % 1000000007;
        }
    }

    println!("{}", dp[n]);
}
