        Arrays.sort(y);
        int ans = 0;
        int i = 0;
        int j = 0;
        while (i < n && j < m) {
            if (x[i] + y[j] < p) {
                ans += x[i] + y[j];
                i++;
                j++;
            } else if (x[i] + y[j] == p) {
                ans += x[i] + y[j];
                i++;
                j++;
            } else {
                ans += x[i];
                i++;
            }
