program P1000;

uses
  SysUtils;

const
  MAXN = 20000;

var
  n, m, p: Integer;
  x, y: Array[0..MAXN - 1] of Integer;
  dp: Array[0..MAXN, 0..MAXN] of Integer;

function max(a, b: Integer): Integer;
begin
  if a > b then
    Result := a
  else
    Result := b;
end;

procedure solve;
var
  i, j, k: Integer;
begin
  for i := 0 to n - 1 do
    for j := 0 to m - 1 do
      dp[i, j] := 0;
  dp[0, 0] := 0;
  for i := 0 to n - 1 do
    for j := 0 to m - 1 do
      for k := 0 to min(i, j) do
        dp[i - k, j - k] := max(dp[i - k, j - k], dp[i, j] + x[i - k] * y[j - k] % p);
  Writeln(dp[n - 1, m - 1]);
  for i := n - 1 downto 0 do
  begin
    for j := m - 1 downto 0 do
      if dp[i, j] = dp[i - 1, j] then
        Write('S');
      else
        Write('C');
    Writeln;
  end;
end;

begin
  Readln(n, m, p);
  for i := 0 to n - 1 do
    Read(x[i]);
  for i := 0 to m - 1 do
    Read(y[i]);
  solve;
end.
