const n = parseInt(stdin[0].split(' ')[0]);
const m = parseInt(stdin[0].split(' ')[1]);
const p = parseInt(stdin[0].split(' ')[2]);
const x = stdin[1].split(' ').map(x => parseInt(x));
const y = stdin[2].split(' ').map(y => parseInt(y));
const dp = new Array(n + m + 1).fill(0);
