import java.util.*

fun main() {
    val (n, m, p) = readLine().split(" ").map { it.toInt() }
    val xs = readLine().split(" ").map { it.toInt() }.sorted()
    val ys = readLine().split(" ").map { it.toInt() }.sorted()
    val dp = Array(n + m - 2) { IntArray(p + 1) }
    dp[0][0] = 0
    for (i in 1..n) {
        for (j in 0..p) {
            dp[i][j] = dp[i - 1][j]
            if (j - xs[i - 1] >= 0) {
                dp[i][j] = maxOf(dp[i][j], dp[i - 1][j - xs[i - 1]] + ys[i - 1])
            }
        }
    }
    val ans = dp[n][p]
    val ansStr = StringBuilder()
    for (i in 0..n - 1) {
        if (ans % 2 == 1) {
            ansStr.append("C")
        } else {
            ansStr.append("S")
