package main

import (
	"fmt"
	"sort"
)

func main() {
	var n, m int
	fmt.Scanf(" %d %d ", &n, &m)
	a := make([]int, n)
	for i := 0; i < n; i++ {
		fmt.Scanf(" %d ", &a[i])
	}
	ms := make([][]int, m)
	for i := 0; i < m; i++ {
		var u, v int
		fmt.Scanf(" %d %d ", &u, &v)
		ms[i] = []int{u - 1, v - 1}
	}
	sort.Slice(a, func(i, j int) bool { return a[i] < a[j] })
	sort.Slice(ms, func(i, j int) bool { return ms[i][0] < ms[j][0] })
	ans := 1000000000
	for i := 0; i < m; i++ {
		for j := i + 1; j < m; j++ {
			if ms[i][1] == ms[j][0] {
				ans = min(ans, a[ms[i][0]] + a[ms[j][1]])
			}
		}
	}
	if ans == 1000000000 {
		fmt.Println("-1")
	} else {
		fmt.Println(ans)
	}
}

func min(a, b int) int {
	if a < b {
		return a
	}
	return b
}

