fn main() {
    let (n, m) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let a = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let mut mp = HashMap::new();
    for _ in 0..m {
        let (u, v) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
        mp.entry(u).or_insert(vec![]).push(v);
        mp.entry(v).or_insert(vec![]).push(u);
    }
    let mut ans = 1000000000000000000;
    for i in 0..n {
        for j in 0..n {
            if i == j {
                continue;
            }
            for k in 0..n {
                if i == k || j == k {
                    continue;
                }
                if mp.contains_key(&i) && mp.contains_key(&j) && mp.contains_key(&k) {
                    let mut s = a[i] + a[j] + a[k];
                    for v in mp.get(&i).unwrap() {
                        if v != j && v != k {
                            s += a[v];
                        }
                    }
                    for v in mp.get(&j).unwrap() {
                        if v != i && v != k {
                            s += a[v];
