using System;
using System.Collections.Generic;

class Program
{
    static void Main(string[] args)
    {
        int n, m;
        string[] line;
        int[] a, b;
        int[] roots;
        int[] count;
        int i, j, k;

        // Read input
        Console.ReadLine();
        line = Console.ReadLine().Split(' ');
        n = int.Parse(line[0]);
        m = int.Parse(line[1]);
        a = new int[m];
        b = new int[m];
        for (i = 0; i < m; i++)
        {
            line = Console.ReadLine().Split(' ');
            a[i] = int.Parse(line[0]);
            b[i] = int.Parse(line[1]);
        }

        // Find roots
        roots = new int[n];
        count = new int[n];
        for (i = 0; i < n; i++)
        {
            roots[i] = -1;
            count[i] = 0;
        }
        for (i = 0; i < m; i++)
        {
            if (roots[a[i]] == -1)
            {
                roots[a[i]] = b[i];
                count[b[i]]++;
            }
            else if (roots[b[i]] == -1)
            {
                roots[b[i]] = a[i];
                count[a[i]]++;
            }
            else if (roots[a[i]] != roots[b[i]])
            {
                Console.WriteLine("NO");
                return;
            }
        }

        // Check if there are at least three roots
        int rootsCount = 0;
        for (i = 0; i < n; i++)
        {
            if (roots[i] != -1)
            {
                rootsCount++;
            }
        }
        if (rootsCount < 3)
        {
            Console.WriteLine("NO");
            return;
        }

        // Check if the graph is a tree
        for (i = 0; i < n; i++)
        {
            if (count[i] != 1)
            {
                Console.WriteLine("NO");
                return;
            }
        }

        // Check if the graph is a simple cycle
        for (i = 0; i < n; i++)
        {
            for (j = 0; j < n; j++)
            {
                if (i != j && roots[i] == roots[j] && i < j)
                {
                    Console.WriteLine("NO");
                    return;
                }
            }
        }

        // The graph is Cthulhu
        Console.WriteLine("FHTAGN!");
    }
}

