uses
  SysUtils;

const
  MAXN = 100;

type
  TVertex = record
    v: Integer;
    parent: Integer;
  end;

var
  n, m, i, j, k, v1, v2: Integer;
  a: array[0..MAXN] of TVertex;
  used: array[0..MAXN] of Boolean;

procedure DFS(v: Integer);
var
  i: Integer;
begin
  used[v] := True;
  for i := 0 to n - 1 do
    if (a[v].v = a[i].v) and (a[v].parent <> i) then
      DFS(i);
end;

begin
  readln(n, m);
  for i := 0 to n - 1 do
  begin
    a[i].v := i;
    a[i].parent := -1;
    used[i] := False;
  end;
  for i := 0 to m - 1 do
  begin
    readln(v1, v2);
    a[v1].v := v2;
    a[v2].v := v1;
  end;
  for i := 0 to n - 1 do
    if not used[i] then
    begin
      DFS(i);
      break;
    end;
