    if (!graph.has(a)) {
        graph.set(a, []);
    }
    if (!graph.has(b)) {
        graph.set(b, []);
    }
    graph.get(a).push(b);
    graph.get(b).push(a);
}
const roots = [];
for (const [key, value] of graph.entries()) {
    if (value.length == 1) {
        roots.push(key);
    }
}
if (roots.length < 3) {
    console.log('NO');
} else {
    const cycle = new Set();
    for (const root of roots) {
        const queue = [root];
        while (queue.length) {
            const current = queue.shift();
            if (cycle.has(current)) {
                console.log('FHTAGN!');
                return;
            }
            cycle.add(current);
            for (const neighbor of graph.get(current)) {
                if (!cycle.has(neighbor)) {
                    queue.push(neighbor);
                }
            }
        }
    }
    console.log('NO');
}

Kotlin:
""
import java.util.*

fun main() {
    val (n, m) = readLine().split(" ").map { it.toInt() }
    val graph = Array(n) { mutableListOf<Int>() }
    for (i in 0 until m) {
        val (a, b) = readLine().split(" ").map { it.toInt() }
        graph[a - 1].add(b - 1)
        graph[b - 1].add(a - 1)
    }

    val visited = BooleanArray(n)
    val cycle = BooleanArray(n)
    val roots = mutableListOf<Int>()
    for (i in 0 until n) {
        if (!visited[i]) {
            dfs(i, graph, visited, cycle, roots)
        }
    }

    if (roots.size < 3) {
        println("NO")
    } else {
        val cycleRoots = roots.filter { cycle[it] }
        if (cycleRoots.size == 0) {
            println("NO")
        } else {
            println("FHTAGN!")
        }
    }
}

private fun dfs(
    v: Int,
    graph: Array<MutableList<Int>>,
    visited: BooleanArray,
    cycle: BooleanArray,
    roots: MutableList<Int>
) {
    visited[v] = true
    for (w in graph[v]) {
        if (!visited[w]) {
            dfs(w, graph, visited, cycle, roots)
        } else if (!cycle[w]) {
            cycle[v] = true
            roots.add(v)
        }
    }
}
