use std::collections::HashMap;

fn main() {
    let (n, m) = read_input();
    let mut graph = HashMap::new();
    for _ in 0..m {
        let (a, b) = read_input();
        graph.entry(a).or_insert(Vec::new()).push(b);
        graph.entry(b).or_insert(Vec::new()).push(a);
    }

    let mut roots = Vec::new();
    for i in 0..n {
        if graph.get(&i).is_none() {
            roots.push(i);
        }
    }

    if roots.len() < 3 {
        println!("NO");
        return;
    }

    let mut cycle = Vec::new();
    for i in 0..roots.len() {
        let mut visited = vec![false; n];
        let mut stack = vec![roots[i]];
        while let Some(v) = stack.pop() {
            if visited[v] {
                continue;
            }
            visited[v] = true;
            cycle.push(v);
            for &w in graph.get(&v).unwrap() {
                if !visited[w] {
                    stack.push(w);
                }
            }
        }
    }

    if cycle.len() != n {
        println!("NO");
        return;
    }

    for i in 0..n {
        if cycle[i] != cycle[(i + 1) % n] {
            println!("NO");
            return;
        }
    }

    println!("FHTAGN!");
}

fn read_input() -> (usize, usize) {
    let mut input = String::new();
    std::io::stdin().read_line(&mut input).unwrap();
    let mut iter = input.split_whitespace();
    let n = iter.next().unwrap().parse().unwrap();
    let m = iter.next().unwrap().parse().unwrap();
    (n, m)
}
