  MAXN = 16;

type
  TPair = record
    A, B: Integer;
  end;

var
  N, Y, M: Integer;
  A: array[0..MAXN] of Integer;
  P: array[0..MAXN] of TPair;

function Next(X: Integer): Integer;
begin
  if X = N then
    X := 0
  else
    X := X + 1;
end;

function Less(A, B: Integer): Boolean;
begin
  if A < B then
    Result := True
  else if A > B then
    Result := False
  else
    Result := A <> B;
end;

function IsValid(Y: Integer): Boolean;
var
  I, J: Integer;
begin
  for I := 0 to M - 1 do
    if P[I].A < P[I].B then
      if A[P[I].A] > A[P[I].B] then
        Result := False;
  Result := True;
end;

function Solve(Y: Integer): Boolean;
var
  I, J: Integer;
begin
  for I := 0 to N - 1 do
    A[I] := I + 1;
  for J := 0 to Y - 1 do
    for I := 0 to N - 1 do
      if A[I] < A[Next(I)] then
        A[I] := A[Next(I)];
  Result := IsValid(Y);
end;

