        seniors[b - 1] -= 1;
    }
    let mut s = HashMap::new();
    for i in 0..n {
        if seniors[i] == 0 {
            s.insert(i + 1, 0);
        }
    }
    let mut ans = vec![];
    while !s.is_empty() {
        let (k, v) = s.iter().next().unwrap();
        ans.push(*k);
        s.remove(k);
        for (k, v) in s.iter_mut() {
            v += 1;
        }
    }
    for i in 0..n {
        print!("{}", ans[i]);
        if i != n - 1 {
            print!(" ");
        }
    }
    println!();
}

