def count_changes(h, m):
    return (h + m // m) * m

def count_changes_in_range(h1, m1, h2, m2):
    return count_changes(h2, m2) - count_changes(h1 - 1, m1 - 1)

def count_changes_in_range_with_k_digits(h1, m1, h2, m2, k):
    return count_changes_in_range(h1, m1, h2, m2) - count_changes_in_range(h1, m1, h2, m2 - k + 1) - count_changes_in_range(h1, m1 - k + 1, h2, m2) + count_changes_in_range(h1, m1 - k + 1, h2, m2 - k + 1)

