def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    return a * b // gcd(a, b)

def modinv(a, m):
    b = m
    u = 1
    v = 0
    while b:
        t = a // b
        a -= t * b
        u -= t * v
        b, a, u, v = a, b, u, v
    return u % m

def solve(a, w, m):
    ans = [0] * n
    for i in range(n):
        if a[i]:
            ans[i] = w[i] + m
        else:
            ans[i] = w[i] - m
    ans = [ans[i] % 998244353 for i in range(n)]
    ans = [ans[i] * modinv(m, 998244353) for i in range(n)]
    ans = [ans[i] % 998244353 for i in range(n)]
    return ans

print(*solve(a, w, m))
