program ProblemB;

uses
  SysUtils;

const
  MOD = 998244353;

var
  n, m, i, j, k: Integer;
  a: Array[0..200000] of Integer;
  w: Array[0..200000] of Integer;
  p: Array[0..200000] of Integer;
  q: Array[0..200000] of Integer;
  s: Array[0..200000] of Integer;
  t: Array[0..200000] of Integer;

function gcd(a, b: Integer): Integer;
begin
  if b = 0 then
    gcd := a
  else
    gcd := gcd(b, a mod b);
end;

function lcm(a, b: Integer): Integer;
begin
  return a * b div gcd(a, b);
end;

procedure init;
var
  i: Integer;
begin
  Readln(n, m);
  for i := 0 to n - 1 do
    Read(a[i]);
  for i := 0 to n - 1 do
    Read(w[i]);
end;

procedure solve;
var
  i, j, k: Integer;
  s1, s2, t1, t2: Integer;
begin
  for i := 0 to n - 1 do
  begin
    s[i] := 0;
    t[i] := 0;
  end;
  for i := 0 to n - 1 do
  begin
    if a[i] = 1 then
    begin
      s[i] := 1;
      t[i] := w[i];
    end;
  end;
  for i := 0 to n - 1 do
  begin
    s1 := 0;
    s2 := 0;
    t1 := 0;
    t2 := 0;
    for j := 0 to n - 1 do
    begin
      if i = j then
        continue;
      if a[j] = 1 then
      begin
        s1 := s1 + s[j];
        t1 := t1 + t[j];
      end
      else
      begin
        s2 := s2 + s[j];
        t2 := t2 + t[j];
      end;
    end;
    p[i] := lcm(s1, t2);
    q[i] := s1 * t2;
  end;
  for i := 0 to n - 1 do
  begin
    Write(p[i] div gcd(p[i], q[i]) mod MOD, ' ');
  end;
end;

begin
