func main() {
	var n, k int
	fmt.Scanf(" %d %d ", &n, &k)
	s := make([]int, n)
	for i := 0; i < n; i++ {
		fmt.Scanf(" %d ", &s[i])
	}
	ans := math.MaxInt32
	for i := 0; i < n; i++ {
		for j := i + 1; j < n; j++ {
			if s[i] == s[j] {
				for k := 0; k < n; k++ {
					if k != i && k != j {
						s[k] = s[i]
					}
				}
				if len(set(s)) >= k {
					ans = min(ans, sum(s))
				}
				for k := 0; k < n; k++ {
					if k != i && k != j {
						s[k] = s[k] - 1
					}
				}
			}
		}
	}
	fmt.Println(ans)
	fmt.Println(join(s))
}

func sum(s []int) int {
	ans := 0
	for i := 0; i < n; i++ {
		ans += abs(s[i], s[0])
	}
	return ans
}

func abs(a, b int) int {
	if a > b {
		return a - b
	}
	return b - a
}

func set(s []int) map[int]int {
	m := make(map[int]int)
	for i := 0; i < n; i++ {
		m[s[i]]++
	}
	return m
}

func join(s []int) string {
	ans := ""
	for i := 0; i < n; i++ {
		ans += fmt.Sprintf("%d", s[i])
	}
	return ans
}

func min(a, b int) int {
	if a < b {
		return a
	}
	return b
}
}"

