    static int DFS(int v, int[] visited)
    {
        visited[v] = 1;
        int count = 1;

        for (int i = 0; i < m; i++)
        {
            if (a[i] == v && visited[b[i]] == 0)
            {
                count += DFS(b[i], visited);
            }
            else if (b[i] == v && visited[a[i]] == 0)
            {
                count += DFS(a[i], visited);
            }
        }

        return count;
    }
