#!/usr/bin/perl -w

use strict;

# Read the number of vertices and edges from the input.
($n, $m) = split /\s+/, <STDIN>;

# Create an adjacency list for the graph.
$adj = [ ];
for (my $i = 0; $i < $n; $i++) {
    $adj[$i] = [];
}

# Read the edges from the input and add them to the adjacency list.
for (my $i = 0; $i < $m; $i++) {
    ($a, $b) = split /\s+/, <STDIN>;
    $adj[$a - 1]->[$b - 1] = 1;
    $adj[$b - 1]->[$a - 1] = 1;
}

# Find all the simple cycles in the graph.
$count = 0;
for (my $i = 0; $i < $n; $i++) {
    # Find all the vertices that are reachable from vertex $i.
    $visited = [ ];
    $visited[$i] = 1;
    $dfs($i, $visited);

    # Count the number of simple cycles that pass through vertex $i.
    $count += $count_cycles($visited);
}

# Print the number of simple cycles in the graph.
print $count;

# Recursive function to find all the vertices that are reachable from a given vertex.
sub dfs($v, $visited) {
    # Recursively visit all the vertices that are adjacent to vertex $v.
    for (my $i = 0; $i < @{$adj[$v]}; $i++) {
        my $w = $adj[$v]->[$i];
        if (! $visited[$w]) {
            $visited[$w] = 1;
            dfs($w, $visited);
        }
    }
}

# Recursive function to count the number of simple cycles that pass through a given set of vertices.
sub count_cycles($visited) {
    # If the set of vertices contains only one vertex, then there is one simple cycle.
    if (@$visited == 1) {
        return 1;
    }

    # Otherwise, there are no simple cycles that pass through the set of vertices.
    return 0;
}

