use std::collections::HashMap;

fn main() {
    // Read input
    let mut n_v: usize = 0;
    let mut n_e: usize = 0;
    std::io::stdin().read_line(&mut n_v.to_string()).unwrap();
    std::io::stdin().read_line(&mut n_e.to_string()).unwrap();

    // Create adjacency list
    let mut adj_list: HashMap<usize, Vec<usize>> = HashMap::new();
    for _ in 0..n_e {
        let mut line: String = String::new();
        std::io::stdin().read_line(&mut line).unwrap();
        let mut v: Vec<usize> = line.split_whitespace().map(|x| x.parse().unwrap()).collect();
        adj_list.entry(v[0]).or_insert(Vec::new()).push(v[1]);
        adj_list.entry(v[1]).or_insert(Vec::new()).push(v[0]);
    }

    // Find all cycles
    let mut cycles: Vec<Vec<usize>> = Vec::new();
    for v in 0..n_v {
        let mut visited: Vec<bool> = vec![false; n_v];
        let mut stack: Vec<usize> = vec![v];
        while !stack.is_empty() {
            let u = stack.pop().unwrap();
            if visited[u] {
                continue;
            }
            visited[u] = true;
            for &v in &adj_list[u] {
                if !visited[v] {
                    stack.push(v);
                }
            }
            if stack.len() >= 3 {
                cycles.push(stack.clone());
            }
        }
    }

    // Print output
    println!("{}", cycles.len());
}
