use std::io;

fn main() {
    let mut stdin = io::stdin();
    let mut stdout = io::stdout();

    let mut n = 0;
    let mut m = 0;

    stdin.read_line(&mut n).unwrap();
    stdin.read_line(&mut m).unwrap();

    let mut graph = vec![vec![0; n + 1]; n + 1];

    for _ in 0..m {
        let mut a = 0;
        let mut b = 0;

        stdin.read_line(&mut a).unwrap();
        stdin.read_line(&mut b).unwrap();

        graph[a as usize][b as usize] = 1;
        graph[b as usize][a as usize] = 1;
    }

    let mut count = 0;

    for i in 0..n {
        let mut visited = vec![false; n + 1];

        let mut queue = vec![i];

        while !queue.is_empty() {
            let current = queue.pop().unwrap();

            if visited[current] {
                continue;
            }

            visited[current] = true;

            for j in 0..n {
                if graph[current][j] == 1 && !visited[j] {
                    queue.push(j);
                }
            }
        }

        if visited.iter().all(|&x| x) {
            count += 1;
        }
    }

    stdout.write_fmt(format_args!("{}", count)).unwrap();
}
""

