namespace Piet
{
    class Program
    {
        static void Main(string[] args)
        {
            string[] input = Console.ReadLine().Split(' ');
            int m = int.Parse(input[0]);
            int n = int.Parse(input[1]);
            int[][] program = new int[m][];
            for (int i = 0; i < m; i++)
            {
                program[i] = Console.ReadLine().Split(' ').Select(int.Parse).ToArray();
            }

            int x = 0;
            int y = 0;
            int d = 1;
            int c = 0;
            for (int i = 0; i < n; i++)
            {
                int x1 = x + d;
                int y1 = y;
                int x2 = x;
                int y2 = y + d;
                if (x1 < 0 || x1 >= m || y1 < 0 || y1 >= program[0].Length)
                {
                    d = (d + 1) % 4;
                }
                else if (x2 < 0 || x2 >= m || y2 < 0 || y2 >= program[0].Length)
                {
                    d = (d + 3) % 4;
                }
                else
                {
                    int max = 0;
                    int maxX = 0;
                    int maxY = 0;
                    for (int j = 0; j < 4; j++)
                    {
                        int x3 = x + d * j;
                        int y3 = y + (d + 2) * j;
                        if (x3 < 0 || x3 >= m || y3 < 0 || y3 >= program[0].Length)
                        {
                            continue;
                        }
                        if (program[x3][y3] > max)
                        {
                            max = program[x3][y3];
                            maxX = x3;
                            maxY = y3;
                        }
                    }
                    x = maxX;
                    y = maxY;
                    c = program[x][y];
                }
            }
            Console.WriteLine(c);
        }
    }
}
