def next_pos(x, y, d, a):
  if d == 1:
    return x + 1, y
  elif d == 2:
    return x, y + 1
  elif d == 3:
    return x - 1, y
  else:
    return x, y - 1

def next_dir(d, c):
  if d == 1 and c == 0:
    return 2
  elif d == 1 and c == 1:
    return 3
  elif d == 2 and c == 0:
    return 1
  elif d == 2 and c == 1:
    return 4
  elif d == 3 and c == 0:
    return 4
  elif d == 3 and c == 1:
    return 1
  elif d == 4 and c == 0:
    return 3
  elif d == 4 and c == 1:
    return 2

def next_c(c, d):
  if d == 1 or d == 3:
    return 1 - c
  else:
    return c

