program Piet;

const
  MaxN = 50;

type
  TColor = 0..9;
  TBlock = record
    Color: TColor;
    Left, Top, Right, Bottom: Integer;
  end;

var
  N, M, n: Integer;
  A: array[0..MaxN, 0..MaxN] of TColor;
  B: array[0..MaxN, 0..MaxN] of TBlock;
  BP, DP, CP: Integer;

procedure ReadInput;
var
  I, J: Integer;
begin
  Readln(N, M);
  for I := 0 to N - 1 do
  begin
    Readln(A[I]);
    for J := 0 to M - 1 do
    begin
      B[I, J].Color := A[I, J];
      B[I, J].Left := J;
      B[I, J].Top := I;
      B[I, J].Right := J;
      B[I, J].Bottom := I;
    end;
  end;
end;

procedure FindBlock(X, Y: Integer; var Block: TBlock);
var
  I, J: Integer;
begin
  Block.Color := A[Y, X];
  Block.Left := X;
  Block.Top := Y;
  Block.Right := X;
  Block.Bottom := Y;
  for I := Y - 1 downto 0 do
    if A[I, X] = Block.Color then
    begin
      Block.Top := I;
      break;
    end;
  for I := Y + 1 to N - 1 do
    if A[I, X] = Block.Color then
    begin
      Block.Bottom := I;
      break;
    end;
  for J := X - 1 downto 0 do
    if A[Y, J] = Block.Color then
    begin
      Block.Left := J;
      break;
    end;
  for J := X + 1 to M - 1 do
    if A[Y, J] = Block.Color then
    begin
      Block.Right := J;
      break;
    end;
end;

procedure Move;
var
  Block: TBlock;
begin
  FindBlock(BP, DP, Block);
  if Block.Right = M - 1 then
  begin
    DP := (DP + 1) mod 4;
    CP := (CP + 1) mod 4;
  end else
  begin
    DP := (DP + 1) mod 4;
  end;
  BP := Block.Right;
end;

procedure Solve;
var
  I: Integer;
begin
  BP := 0;
  DP := 0;
  CP := 1;
  for I := 1 to n do
    Move;
  Writeln(B[BP, DP].Color);
end;

begin
  ReadInput;
  Solve;
end.
""

