	fmt.Scanf(" %d %d ", &m, &n)
	a := make([][]int, m)
	for i := 0; i < m; i++ {
		a[i] = make([]int, n)
		for j := 0; j < n; j++ {
			fmt.Scanf(" %d ", &a[i][j])
		}
	}
	x, y, d, c := 0, 0, 0, 0
	for i := 0; i < n; i++ {
		if a[0][i] != 0 {
			x, y, d, c = 0, i, 0, a[0][i]
			break
		}
	}
	for i := 0; i < n; i++ {
		if a[m-1][i] != 0 {
			x, y, d, c = m-1, i, 2, a[m-1][i]
			break
		}
	}
	for i := 0; i < m; i++ {
		if a[i][0] != 0 {
			x, y, d, c = i, 0, 1, a[i][0]
			break
		}
	}
	for i := 0; i < m; i++ {
		if a[i][n-1] != 0 {
			x, y, d, c = i, n-1, 3, a[i][n-1]
			break
		}
	}
	for i := 0; i < n; i++ {
		if a[x][y] != 0 {
			break
		}
		x, y, d = next(x, y, d)
	}
	fmt.Println(c)
}

func next(x, y, d int) (int, int, int) {
	if d == 0 {
		if y+1 < n && a[x][y+1] != 0 {
			return x, y+1, 0
		} else {
			return x, y, 2
		}
	} else if d == 1 {
		if x+1 < m && a[x+1][y] != 0 {
			return x+1, y, 1
		} else {
			return x, y, 3
		}
	} else if d == 2 {
		if y-1 >= 0 && a[x][y-1] != 0 {
			return x, y-1, 0
		} else {
			return x, y, 1
		}
	} else {
		if x-1 >= 0 && a[x-1][y] != 0 {
			return x-1, y, 3
		} else {
			return x, y, 2
		}
	}
}
