const fs = require('fs');
const input = fs.readFileSync('input.txt', 'utf8').split('\n');
const m = parseInt(input[0].split(' ')[0]);
const n = parseInt(input[0].split(' ')[1]);
const program = input.slice(1);

const dp = [0, 1, 0, -1, 0];
const cp = [-1, 0, 1, 0];

const getColor = (i, j) => {
  if (i < 0 || i >= m || j < 0 || j >= program[0].length) {
    return 0;
  }
  return program[i][j] - '0';
};

const getEdge = (i, j, d) => {
  let x = i;
  let y = j;
  while (true) {
    x += dp[d];
    y += cp[d];
    if (x < 0 || x >= m || y < 0 || y >= program[0].length) {
      return [x, y];
    }
    if (getColor(x, y) !== getColor(i, j)) {
      return [x, y];
    }
  }
};

const getNext = (i, j, d) => {
  const [nx, ny] = getEdge(i, j, d);
  const color = getColor(nx, ny);
  if (color === 0) {
    return [i, j, (d + 2) % 4];
  }
  return [nx, ny, d];
};

const run = (i, j, d, n) => {
  if (n === 0) {
    return getColor(i, j);
  }
  return run(...getNext(i, j, d), n - 1);
};

console.log(run(0, 0, 0, n));
""

