import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class Main {

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input = br.readLine().split(" ");
        int n = Integer.parseInt(input[0]);
        int m = Integer.parseInt(input[1]);
        List<List<Integer>> intervals = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            int k = Integer.parseInt(br.readLine());
            List<Integer> interval = new ArrayList<>();
            for (int j = 0; j < k; j++) {
                input = br.readLine().split(" ");
                interval.add(Integer.parseInt(input[0]));
                interval.add(Integer.parseInt(input[1]));
            }
            intervals.add(interval);
        }
        int[] sums = new int[m + 1];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < intervals.get(i).size(); j++) {
                for (int k = intervals.get(i).get(j); k <= intervals.get(i).get(j + 1); k++) {
                    sums[k]++;
                }
            }
        }
        int max = 0;
        for (int i = 1; i <= m; i++) {
            max = Math.max(max, sums[i] * sums[i]);
        }
        System.out.println(max);
    }
}
