        for (int i = 0; i < n; i++)
        {
            var k = int.Parse(Console.ReadLine());
            var interval = new List<int>();
            for (int j = 0; j < k; j++)
            {
                var input = Console.ReadLine().Split(' ');
                interval.Add(int.Parse(input[0]));
                interval.Add(int.Parse(input[1]));
            }
            intervals.Add(interval.ToArray());
        }

        var max = 0;
        for (int i = 0; i < m; i++)
        {
            var sum = 0;
            for (int j = 0; j < n; j++)
            {
                var interval = intervals[j];
                var left = interval[0];
                var right = interval[1];
                if (i >= left && i <= right)
                {
                    sum++;
                }
            }
            max = Math.Max(max, sum * sum);
        }

        Console.WriteLine(max);
