    for (int i = 0; i < 4 * n; i++) {
        cin >> v[i];
    }

    vector<vector<int>> dp(n, vector<int>(n, 0));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i == j) {
                continue;
            }

            int l = v[i].size() - k;
            int r = v[j].size() - k;
            if (l < 0 || r < 0) {
                continue;
            }

            int s1 = v[i].substr(l, k).compare(v[j].substr(r, k));
            if (s1 == 0) {
                dp[i][j] = 1;
            }
        }
    }

    vector<int> a(n, 0);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i == j) {
                continue;
            }

            if (dp[i][j] == 1) {
                a[i]++;
            }
        }
    }

    int max_a = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] > max_a) {
            max_a = a[i];
