import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {

    public static void main(String[] args) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
        String[] input = reader.readLine().split(" ");
        int n = Integer.parseInt(input[0]);
        int k = Integer.parseInt(input[1]);
        String[] lines = new String[4 * n];
        for (int i = 0; i < 4 * n; i++) {
            lines[i] = reader.readLine();
        }
        int[][][] dp = new int[n][4][k + 1];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 4; j++) {
                for (int l = 0; l <= k; l++) {
                    dp[i][j][l] = -1;
                }
            }
        }
        int[] ans = new int[4];
        for (int i = 0; i < 4; i++) {
            ans[i] = dfs(lines, 0, i, k, dp);
        }
        Arrays.sort(ans);
        if (ans[0] == 0) {
            System.out.println("NO");
        } else if (ans[0] == 1) {
            System.out.println("aabb");
        } else if (ans[0] == 2) {
            System.out.println("abab");
        } else if (ans[0] == 3) {
            System.out.println("abba");
        } else {
            System.out.println("aaaa");
        }
    }

    private static int dfs(String[] lines, int i, int j, int k, int[][][] dp) {
        if (i == 4 * n) {
            return 0;
        }
        if (dp[i][j][k] != -1) {
            return dp[i][j][k];
        }
        int ans = 0;
        for (int l = 0; l < 4; l++) {
            if (l != j && lines[i].charAt(lines[i].length() - k - 1) == lines[i + 1].charAt(lines[i + 1].length() - k - 1)) {
                ans = Math.max(ans, dfs(lines, i + 1, l, k, dp) + 1);
            }
        }
        dp[i][j][k] = ans;
        return ans;
    }
}
