program poem;

uses
  SysUtils;

const
  MAX_N = 2500;
  MAX_K = 5;

type
  TLine = string;
  TQuatrain = array[1..4] of TLine;

var
  n, k: integer;
  quatrains: array[1..MAX_N] of TQuatrain;
  rhymes: array[1..MAX_K] of array[1..MAX_K] of boolean;

procedure init_rhymes;
var
  i, j, l: integer;
begin
  for i := 1 to MAX_K do
    for j := 1 to MAX_K do
      rhymes[i][j] := false;
  for i := 1 to MAX_K do
    rhymes[i][i] := true;
  for i := 1 to MAX_K do
    for j := i + 1 to MAX_K do
      for l := 1 to MAX_K do
        if (l < i) or (l > j) then
          rhymes[i][j] := rhymes[i][j] or ('a' + l = 'a' + i) or ('a' + l = 'a' + j);
end;

function get_rhyme_scheme(quatrain: TQuatrain): string;
var
  i, j: integer;
begin
  for i := 1 to 4 do
    for j := 1 to 4 do
      if (i <> j) and (rhymes[quatrain[i].Length - k][quatrain[j].Length - k]) then
        return 'NO';
  if (quatrain[1].Length - k = 0) or (quatrain[2].Length - k = 0) or
     (quatrain[3].Length - k = 0) or (quatrain[4].Length - k = 0) then
    return 'NO';
  if (quatrain[1].Length - k = quatrain[2].Length - k) and
     (quatrain[2].Length - k = quatrain[3].Length - k) and
     (quatrain[3].Length - k = quatrain[4].Length - k) then
    return 'aaaa';
  if (quatrain[1].Length - k = quatrain[2].Length - k) and
     (quatrain[3].Length - k = quatrain[4].Length - k) then
    return 'aabb';
  if (quatrain[1].Length - k = quatrain[3].Length - k) and
     (quatrain[2].Length - k = quatrain[4].Length - k) then
    return 'abab';
  if (quatrain[1].Length - k = quatrain[4].Length - k) and
     (quatrain[2].Length - k = quatrain[3].Length - k) then
    return 'abba';
end;

begin
  init_rhymes;
  readln(n, k);
  for i := 1 to n do
    readln(quatrains[i][1]);
  for i := 2 to 4 do
    readln(quatrains[i]);
  writeln(get_rhyme_scheme(quatrains[1]));
end.
