const input = fs.readFileSync('input.txt', 'utf-8').split('\n');
const n = Number(input[0].split(' ')[0]);
const k = Number(input[0].split(' ')[1]);
const lines = [];
for (let i = 1; i <= n * 4; i++) {
  lines.push(input[i]);
}
const s = [];
for (let i = 0; i < n; i++) {
  const a = lines[i * 4];
  const b = lines[i * 4 + 1];
  const c = lines[i * 4 + 2];
  const d = lines[i * 4 + 3];
  const a1 = a.substr(-k);
  const b1 = b.substr(-k);
  const c1 = c.substr(-k);
  const d1 = d.substr(-k);
  if (a1 === b1 && b1 === c1 && c1 === d1) {
    s.push('aaaa');
  } else if (a1 === b1 && b1 !== c1 && c1 === d1) {
    s.push('aabb');
  } else if (a1 === b1 && b1 === c1 && c1 !== d1) {
    s.push('abab');
  } else if (a1 !== b1 && b1 === c1 && c1 === d1) {
    s.push('abba');
  } else {
    s.push('NO');
  }
}
console.log(s.join('\n'));
