fn main() {
    let (n, k) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let mut lines = Vec::new();
    for _ in 0..n {
        lines.push(read_line().unwrap());
    }

    let mut m = HashMap::new();
    for i in 0..n {
        let mut suffix = lines[i].chars().rev().skip(k).collect::<Vec<_>>();
        suffix.sort();
        let key = suffix.join("");
        if m.contains_key(&key) {
            println!("NO");
            return;
        }
        m.insert(key, i);
    }

    let mut a = 0;
    let mut b = 0;
    let mut c = 0;
    let mut d = 0;
    for i in 0..n {
        let j = (i + 1) % n;
        let k = (i + 2) % n;
        let l = (i + 3) % n;
        if m[&lines[i].chars().rev().skip(k).collect::<Vec<_>>().join("")] == j {
            a += 1;
        }
        if m[&lines[i].chars().rev().skip(k).collect::<Vec<_>>().join("")] == k {
