        int u, v;
        cin >> u >> v;
        u--;
        v--;
        g[u].push_back(v);
        g[v].push_back(u);
    }

    vector<int> dp(n, 1e9);
    dp[0] = 0;
    for (int i = 0; i < n; i++) {
        for (int j : g[i]) {
            dp[j] = min(dp[j], dp[i] + t[j]);
        }
    }

    int ans = 1e9;
    for (int i = 0; i < n; i++) {
        for (int j : g[i]) {
            ans = min(ans, dp[i] + dp[j] + h[i] - h[j]);
        }
    }
    cout << ans << endl;
    return 0;
}
