        int[] outd = new int[n];
        int[] ans = new int[n];
        Arrays.fill(ans, Integer.MAX_VALUE);
        PriorityQueue<int[]> pq = new PriorityQueue<>(Comparator.comparingInt(a -> a[0]));
        String[] s = br.readLine().split(" ");
        for (int i = 0; i < n; i++) {
            t[i] = Integer.parseInt(s[i]);
        }
        s = br.readLine().split(" ");
        for (int i = 0; i < n; i++) {
            h[i] = Integer.parseInt(s[i]);
        }
        for (int i = 0; i < n - 1; i++) {
            s = br.readLine().split(" ");
            int u = Integer.parseInt(s[0]) - 1;
            int v = Integer.parseInt(s[1]) - 1;
            ind[v]++;
            outd[u]++;
            pq.add(new int[]{h[u] - h[v], u, v});
        }
        int cnt = 0;
        while (!pq.isEmpty()) {
            int[] cur = pq.poll();
            int h1 = cur[0];
            int u = cur[1];
            int v = cur[2];
            if (ind[u] == 0 && outd[v] == 0) {
                ans[cnt] = t[u] + t[v];
                cnt++;
            } else if (ind[u] == 0) {
                ans[cnt] = t[u] + t[v];
                ind[v]--;
            } else if (outd[v] == 0) {
                ans[cnt] = t[u] + t[v];
                outd[u]--;
            }
        }
        System.out.println(Arrays.stream(ans).min().getAsInt());
    }
}
