#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

int n, t[200005], h[200005], dp[200005], ans;
int s[200005], e[200005];
int scc[200005], scc_cnt;
int adj[200005];
int in[200005], out[200005];

void dfs(int u) {
    s[u] = ++scc_cnt;
    for (int i = 0; i < adj[u]; i++) {
        int v = e[i];
        if (s[v] == 0) {
            dfs(v);
        }
    }
    e[scc_cnt] = u;
}

int main() {
    scanf(" %d ", &n);
    for (int i = 1; i <= n; i++) {
        scanf(" %d ", &t[i]);
    }
    for (int i = 1; i <= n; i++) {
        scanf(" %d ", &h[i]);
    }
    for (int i = 1; i <= n - 1; i++) {
        int u, v;
        scanf(" %d %d ", &u, &v);
        adj[u]++;
        adj[v]++;
    }
    for (int i = 1; i <= n; i++) {
        if (s[i] == 0) {
            dfs(i);
        }
    }
    for (int i = 1; i <= n; i++) {
        dp[scc[i]] = t[i];
    }
    for (int i = scc_cnt - 1; i >= 1; i--) {
        for (int j = 0; j < adj[e[i]]; j++) {
            int v = e[i + 1 + j];
            if (scc[v] < scc[e[i]]) {
                dp[scc[e[i]]] = max(dp[scc[e[i]]], dp[scc[v]] + t[e[i]]);
            }
        }
    }
    for (int i = 1; i <= scc_cnt; i++) {
        ans = max(ans, dp[i]);
    }
    printf(" %d ", ans);
    return 0;
}
