    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());
        int[] t = Array.ConvertAll(Console.ReadLine().Split(' '), int.Parse);
        int[] h = Array.ConvertAll(Console.ReadLine().Split(' '), int.Parse);
        List<int[]> edges = new List<int[]>();
        for (int i = 0; i < n - 1; i++)
        {
            int[] edge = Array.ConvertAll(Console.ReadLine().Split(' '), int.Parse);
            edges.Add(edge);
        }

        int[] dp = new int[n];
        dp[0] = t[0];
        for (int i = 1; i < n; i++)
        {
            dp[i] = dp[i - 1] + t[i];
        }

        int ans = int.MaxValue;
        for (int i = 0; i < n - 1; i++)
        {
            int[] edge = edges[i];
            int u = edge[0];
            int v = edge[1];
            int t1 = dp[u - 1];
            int t2 = dp[v - 1];
            int t3 = t[u] + t[v];
            int t4 = t1 + t2;
            int t5 = t1 + t3;
            int t6 = t2 + t3;
            ans = Math.Min(ans, t1 + t4 + t5);
            ans = Math.Min(ans, t1 + t4 + t6);
            ans = Math.Min(ans, t1 + t5 + t6);
            ans = Math.Min(ans, t2 + t4 + t5);
            ans = Math.Min(ans, t2 + t4 + t6);
            ans = Math.Min(ans, t2 + t5 + t6);
        }

        Console.WriteLine(ans);
    }
}
