program Boboniu;

uses
  SysUtils;

const
  MAXN = 200000;

type
  TEdge = record
    u, v: Integer;
  end;

var
  n, m: Integer;
  t, h: array[0..MAXN] of Integer;
  e: array[0..MAXN] of TEdge;
  adj: array[0..MAXN] of TEdge;
  vis: array[0..MAXN] of Boolean;
  dp: array[0..MAXN] of Integer;

procedure dfs(u: Integer);
var
  i, v: Integer;
begin
  vis[u] := True;
  for i := 0 to m - 1 do
    if not vis[e[i].v] and h[e[i].v] > h[u] then
    begin
      adj[e[i].v].u := u;
      dfs(e[i].v);
    end;
end;

function solve(): Integer;
var
  i, j, k, ans: Integer;
begin
  ans := 0;
  for i := 1 to n do
  begin
    dp[i] := t[i];
    for j := 0 to m - 1 do
      if adj[e[j].v].u = i then
        dp[i] := min(dp[i], dp[adj[e[j].v].u] + t[e[j].v]);
    ans := max(ans, dp[i]);
  end;
  exit(ans);
end;

begin
  readln(n);
  for i := 1 to n do
    readln(t[i]);
  for i := 1 to n do
    readln(h[i]);
  for i := 0 to m - 1 do
  begin
    readln(e[i].u, e[i].v);
    e[i].u := e[i].u - 1;
    e[i].v := e[i].v - 1;
  end;
  for i := 1 to n do
    adj[i].u := -1;
  dfs(0);
  writeln(solve());
end.
