use std::collections::{BTreeMap, BTreeSet};

fn main() {
    // 读入数据
    let n = read_line::<usize>();
    let t = read_line::<Vec<usize>>();
    let h = read_line::<Vec<usize>>();
    let mut g = vec![vec![]; n];
    for _ in 0..n - 1 {
        let (u, v) = read_line::<Vec<usize>>().into_iter().map(|x| x as usize).collect_tuple().unwrap();
        g[u - 1].push(v - 1);
        g[v - 1].push(u - 1);
    }

    // 构造并求解最小生成树
    let mut mst = BTreeMap::new();
    let mut pq = BTreeSet::new();
    for i in 0..n {
        pq.insert((h[i], i));
    }
    while let Some((_, u)) = pq.pop() {
        for &v in &g[u] {
            if let Some(&w) = mst.get(&v) {
                if w > h[u] {
                    mst.insert(v, h[u]);
                    pq.insert((h[v], v));
                }
            } else {
                mst.insert(v, h[u]);
                pq.insert((h[v], v));
            }
        }
    }

    // 求解答案
    let mut ans = 0;
    for (_, v) in mst.iter() {
        ans += t[v];
    }
    println!("{}", ans);
}

fn read_line<T>() -> Vec<T> {
    let mut s = String::new();
    std::io::stdin().read_line(&mut s).unwrap();
    s.trim().split_whitespace().map(|x| x.parse().unwrap()).collect()
}
