        int n = sc.nextInt();
        int min = 0;
        int max = 0;
        for (int i = 1; i <= Math.sqrt(n); i++) {
            if (n % i == 0) {
                int a = i;
                int b = n / i;
                int c = Math.min(a, b);
                min = Math.max(min, (a - 1) * (b - 2) * (c - 2));
                max = Math.max(max, a * b * c);
            }
        }
        System.out.println(min + " " + max);
    }
}

