        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        int[][] a = new int[n][m];
        for (int i = 0; i < n; i++) {
            String s = sc.next();
            for (int j = 0; j < m; j++) {
                a[i][j] = s.charAt(j) == 'G' ? 1 : s.charAt(j) == 'R' ? 2 : 0;
            }
        }
        int[][] b = new int[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                b[i][j] = a[i][j];
            }
        }
        boolean first = true;
        while (true) {
            if (first) {
                first = false;
            } else {
                for (int i = 0; i < n; i++) {
                    for (int j = 0; j < m; j++) {
                        b[i][j] = a[i][j];
                    }
                }
            }
            boolean flag = false;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    if (a[i][j] == 1) {
                        if (j + k - 1 < m && a[i][j + k - 1] == 2) {
                            for (int l = j; l <= j + k - 1; l++) {
                                a[i][l] = 0;
                            }
                            flag = true;
                        } else if (j - k + 1 >= 0 && a[i][j - k + 1] == 2) {
                            for (int l = j; l >= j - k + 1; l--) {
                                a[i][l] = 0;
                            }
                            flag = true;
                        }
                    }
                }
            }
            if (!flag) {
                break;
            }
        }
        if (first) {
            System.out.println("First");
        } else {
            System.out.println("Second");
        }
    }
}
