import java.util.Scanner;

public class Main {

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[][] a = new int[n][2];
        for (int i = 0; i < n; i++) {
            a[i][0] = sc.nextInt();
            a[i][1] = sc.nextInt();
        }
        int m = sc.nextInt();
        int[][] b = new int[m][2];
        for (int i = 0; i < m; i++) {
            b[i][0] = sc.nextInt();
            b[i][1] = sc.nextInt();
        }

        int ans = 0;
        for (int i = 0; i < m; i++) {
            int j = i + 1;
            while (j < m && b[j][0] == b[i][0]) {
                j++;
            }
            if (j == m) {
                break;
            }
            int k = j + 1;
            while (k < m && b[k][0] == b[j][0]) {
                k++;
            }
            if (k == m) {
                break;
            }
            int l = j;
            while (l < k) {
                if (isInside(a, b[l])) {
                    ans = 1;
                    break;
                }
                l++;
            }
        }
        System.out.println(ans == 1 ? "YES" : "NO");
    }

    private static boolean isInside(int[][] a, int[] b) {
        int n = a.length;
        int x = b[0];
        int y = b[1];
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            int x1 = a[i][0];
            int y1 = a[i][1];
            int x2 = a[(i + 1) % n][0];
            int y2 = a[(i + 1) % n][1];
            if (y1 <= y && y <= y2) {
                if (x1 <= x && x <= x2) {
                    return true;
                }
                if (x1 <= x && x < x2 && y1 < y) {
                    cnt++;
                }
                if (x2 < x && x <= x1 && y1 < y) {
                    cnt++;
                }
            }
        }
        return cnt % 2 == 1;
    }
}
