func main() {
	var n, m int
	fmt.Scanf("%d\n", &n)
	a := make([][2]int, n)
	for i := 0; i < n; i++ {
		fmt.Scanf(" %d %d\n", &a[i][0], &a[i][1])
	}
	fmt.Scanf("%d\n", &m)
	b := make([][2]int, m)
	for i := 0; i < m; i++ {
		fmt.Scanf(" %d %d\n", &b[i][0], &b[i][1])
	}
	for i := 0; i < m; i++ {
		if !check(a, b[i]) {
			fmt.Println("NO")
			return
		}
	}
	fmt.Println("YES")
}

func check(a, b [][2]int) bool {
	for i := 0; i < n; i++ {
		if cross(a[i], a[(i+1)%n], b[0]) < 0 {
			return false
		}
	}
	for i := 0; i < m; i++ {
		if cross(b[i], b[(i+1)%m], a[0]) > 0 {
			return false
		}
	}
	return true
}

func cross(a, b, c [2]int) int {
	return (b[0]-a[0])*(c[1]-a[1]) - (b[1]-a[1])*(c[0]-a[0])
}
