import std.stdio;
import std.algorithm;
import std.array;

struct Point {
    int x;
    int y;

    Point(int x, int y) {
        this.x = x;
        this.y = y;
    }

    int cross(Point p) {
        return this.x * p.y - this.y * p.x;
    }

    int dot(Point p) {
        return this.x * p.x + this.y * p.y;
    }

    bool operator <(Point p) {
        return this.x < p.x || (this.x == p.x && this.y < p.y);
    }
}

int main() {
    int n;
    scanf(" %d ", &n);
    auto a = new Point[n];
    for (int i = 0; i < n; i++) {
        scanf(" %d %d ", &a[i].x, &a[i].y);
    }

    int m;
    scanf(" %d ", &m);
    auto b = new Point[m];
    for (int i = 0; i < m; i++) {
        scanf(" %d %d ", &b[i].x, &b[i].y);
    }

    auto a_convex = convexHull(a, n);
    auto b_convex = convexHull(b, m);

    int i = 0;
    int j = 0;
    while (i < n && j < m) {
        if (a_convex[i] < b_convex[j]) {
            i++;
        } else if (a_convex[i] > b_convex[j]) {
            j++;
        } else {
            i++;
            j++;
        }
    }

    if (i == n) {
        printf(" YES ");
    } else {
        printf(" NO ");
    }

    return 0;
}

auto convexHull(Point[] points, int n) {
    auto hull = new Point[n];
    int k = 0;

    sort(points);

    for (int i = 0; i < n; i++) {
        while (k >= 2 && hull[k - 2].cross(hull[k - 1]) < points[i].cross(hull[k - 1])) {
            k--;
        }
        hull[k++] = points[i];
    }

    for (int i = n - 2; i >= 0; i--) {
        while (k >= 2 && hull[k - 2].cross(hull[k - 1]) < points[i].cross(hull[k - 1])) {
            k--;
        }
        hull[k++] = points[i];
    }

    return hull;
}
