    vector<bool> used(n + 1, false);
    vector<int> parent(n + 1, -1);
    queue<int> q;
    q.push(1);
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        used[u] = true;
        for (auto v : g[u]) {
            if (!used[v.first] && d[v.first] > d[u] + v.second) {
                d[v.first] = d[u] + v.second;
                parent[v.first] = u;
                q.push(v.first);
            }
        }
    }
    int ans = 0;
