""
import std.algorithm;
import std.array;
import std.conv;
import std.stdio;

struct Edge {
    int from;
    int to;
    int weight;
}

struct Graph {
    int n;
    int m;
    Edge[] edges;

    Graph(int n, int m, Edge[] edges) {
        this.n = n;
        this.m = m;
        this.edges = edges;
    }

    int[] dijkstra(int start) {
        int[] dist = new int[n];
        fill(dist, -1);
        dist[start] = 0;

        int[] queue = new int[n];
        int queueHead = 0;
        int queueTail = 0;

        queue[queueTail++] = start;

        while (queueHead < queueTail) {
            int u = queue[queueHead++];

            for (int i = 0; i < m; i++) {
                int v = edges[i].to;
                int w = edges[i].weight;

                if (dist[v] == -1 || dist[v] > dist[u] + w) {
                    dist[v] = dist[u] + w;
                    queue[queueTail++] = v;
                }
            }
        }

        return dist;
    }
}

int main() {
    int n, m;
    scanf(" %d %d ", &n, &m);

    Edge[] edges = new Edge[m];
    for (int i = 0; i < m; i++) {
        int x, y, w;
        scanf(" %d %d %d ", &x, &y, &w);
        edges[i] = new Edge(x - 1, y - 1, w);
    }

    int k;
    scanf(" %d ", &k);

    int[] portals = new int[k];
    for (int i = 0; i < k; i++) {
        int p;
        scanf(" %d ", &p);
        portals[i] = p - 1;
    }

    Graph graph = new Graph(n, m, edges);

    int[] dist = graph.dijkstra(0);

    int ans = 0;
    for (int i = 0; i < k; i++) {
        ans = max(ans, dist[portals[i]]);
    }

    printf(" %d ", ans);

    return 0;
}
""

