use std::collections::{HashMap, HashSet};

fn main() {
    let (n, m) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let mut graph = HashMap::new();
    for _ in 0..m {
        let (a, b, c) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
        graph.entry(a).or_insert(Vec::new()).push((b, c));
        graph.entry(b).or_insert(Vec::new()).push((a, c));
    }
    let k = read_line().unwrap().parse().unwrap();
    let portals = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let mut visited = HashSet::new();
    let mut ans = 0;
    for p in portals {
        if !visited.contains(&p) {
            visited.insert(p);
            ans += dfs(graph, p, 0);
        }
    }
    println!("{}", ans);
}

fn dfs(graph: &HashMap<i32, Vec<(i32, i32)>>, p: i32, d: i32) -> i32 {
    let mut ans = d;
    for (q, w) in graph.get(&p).unwrap() {
        if !visited.contains(&q) {
            visited.insert(q);
            ans = ans.max(dfs(graph, q, d + w));
        }
    }
    ans
}

fn read_line() -> String {
    let mut s = String::new();
    std::io::stdin().read_line(&mut s).unwrap();
    s.trim().to_string()
}
