fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();

    let (n, d) = lines
        .next()
        .unwrap()
        .split_whitespace()
        .map(|s| s.parse::<usize>().unwrap())
        .collect::<Vec<_>>();

    let (a, b) = lines
        .next()
        .unwrap()
        .split_whitespace()
        .map(|s| s.parse::<usize>().unwrap())
        .collect::<Vec<_>>();

    let mut clients = Vec::new();
    for _ in 0..n {
        let (x, y) = lines
            .next()
            .unwrap()
            .split_whitespace()
            .map(|s| s.parse::<usize>().unwrap())
            .collect::<Vec<_>>();

        clients.push((x, y));
    }

    let mut ans = 0;
    let mut memory = d;
    for (x, y) in clients {
        if memory >= x * a + y * b {
            ans += 1;
            memory -= x * a + y * b;
        }
