    for (int i = 0; i < n; i++) {
        indegree[i] = 0;
        outdegree[i] = 0;
        visited[i] = 0;
        ans[i] = 0;
        adj[i] = new int[n];
    }
    for (int i = 0; i < m; i++) {
        int a, b;
        scanf(" %d %d ", &a, &b);
        a--;
        b--;
        adj[a][outdegree[a]++] = b;
        adj[b][outdegree[b]++] = a;
    }
    int start = 0;
    for (int i = 0; i < n; i++) {
        if (indegree[i] == 0) {
            start = i;
            break;
        }
    }
    int count = 0;
    int cur = start;
    while (true) {
        if (visited[cur] == 1) {
            break;
        }
        visited[cur] = 1;
        count++;
        for (int i = 0; i < outdegree[cur]; i++) {
            int next = adj[cur][i];
            indegree[next]--;
            if (indegree[next] == 0) {
